const getPreferredTheme=()=>{const e=localStorage.getItem("theme");return e?e:window.matchMedia("(prefers-color-scheme: dark)").matches?"dark":"light"},applyTheme=e=>{console.log("Applying theme:",e),document.documentElement.setAttribute("data-theme",e),localStorage.setItem("theme",e)},initializeTheme=()=>{const e=document.getElementById("theme-toggle");if(!e){console.error("Theme toggle button not found");return}const t=getPreferredTheme();console.log("Initial theme:",t),applyTheme(t),e.addEventListener("click",e=>{console.log("Toggle clicked");const t=document.documentElement.getAttribute("data-theme");console.log("Current theme:",t);const n=t==="dark"?"light":"dark";console.log("New theme:",n),applyTheme(n)})};document.readyState==="loading"?document.addEventListener("DOMContentLoaded",initializeTheme):initializeTheme()