const getPreferredTheme=()=>{const e=localStorage.getItem("theme");return e?e:window.matchMedia("(prefers-color-scheme: dark)").matches?"dark":"light"},applyTheme=e=>{document.documentElement.setAttribute("data-theme",e),localStorage.setItem("theme",e)};document.addEventListener("DOMContentLoaded",()=>{const e=document.getElementById("theme-toggle");applyTheme(getPreferredTheme()),e.addEventListener("click",()=>{const e=document.documentElement.getAttribute("data-theme"),t=e==="dark"?"light":"dark";applyTheme(t)})})